mapper.select:
SELECT count(*) FROM PLANTS p
, PLANTSTOKEYWORD p2
WHERE 1 = 1
AND p.PLANTSID = p2.PLANTSID AND p2.KEYID = #{keyId}
AND PLANTSNAME LIKE #{term}

SELECT * from( SELECT rownum rn, p.PLANTSID ,p.PLANTSNAME, f2.FILENAME , p2.KEYID FROM
PLANTS p,
(SELECT * FROM plants WHERE PLANTSNAME LIKE #{term}) p ,
PLANTSTOKEYWORD p2 ,
(SELECT f.PLANTSID, f.FILENAME FROM ( SELECT RANK () OVER (PARTITION BY p3.PLANTSID ORDER BY p3.FILENAME) AS RANK, p3.* FROM PLANTFILE p3 ) f WHERE rank = 1) f2 WHERE p.PLANTSID = p2.PLANTSID AND p.PLANTSID = f2.PLANTSID
AND p2.KEYID = #{keyId}
) WHERE rn >= #{start} AND rn <= #{end}

SELECT *
from (
	SELECT rownum rn, p3.*, f2.FILENAME
	FROM PLANTKEYWORD p, PLANTSTOKEYWORD p2, PLANTS p3 , 
				(SELECT f.PLANTSID, f.FILENAME 
					FROM (
						SELECT RANK () OVER (PARTITION BY p4.PLANTSID ORDER BY p4.FILENAME) AS RANK, p4.*
						FROM PLANTFILE p4 
						) f
					WHERE rank = 1) f2
	WHERE p.KEYID = #{keyId}
		AND p.KEYID = p2.KEYID
		AND p2.PLANTSID = p3.PLANTSID
		AND p3.PLANTSID = f2.PLANTSID
)
 WHERE rn >= #{start} AND rn <= #{end}

	  SELECT P.PLANTSNAME,P.PLANTENG,P.PLANTSID, P.PDESCRIPTION FROM
  	  PLANTS  p
	  WHERE P.PLANTSID=#{plantsId}

SELECT DESNO,DESTITLE,DES FROM PLANTDES
WHERE PLANTSID=#{plantsId} 
ORDER BY DESNO

SELECT COUNT(PLANTSID)
FROM USERTOPLANTS
WHERE PLANTSID=#{plantId} AND USERID=#{userId}
